// GetMeta node: produce a single item containing total_bytes, mimeType and fileName
const it = items[0];
if (!it.binary || !it.binary.data) {
  throw new Error("No binary.data found on input item");
}
// note: don't output the base64 itself, only metadata
const base64 = it.binary.data.data;
const total_bytes = Buffer.from(base64, 'base64').length;
const mimeType = it.binary.data.mimeType || 'video/mp4';
const fileName = it.binary.data.fileName || 'file.mp4';

return [{ json: { total_bytes, mimeType, fileName } }];

const results = [];
const CHUNK_SIZE = 4 * 1024 * 1024;

const item = items[0];

// Use the actual key name "datavideo"
if (!item.binary || !item.binary.datavideo) {
  throw new Error("No binary.datavideo found on the input item");
}

const base64 = item.binary.datavideo.data;
const buffer = Buffer.from(base64, 'base64');
const totalSize = buffer.length;

for (let i = 0; i < totalSize; i += CHUNK_SIZE) {
  const chunk = buffer.slice(i, i + CHUNK_SIZE);
  results.push({
    json: {
      index: Math.floor(i / CHUNK_SIZE),
      size: chunk.length,
    },
    binary: {
      chunk: {
        data: chunk.toString('base64'),
        mimeType: item.binary.datavideo.mimeType,
        fileName: item.binary.datavideo.fileName || `chunk_${i}.bin`,
      }
    }
  });
}

return results;

const rows = $input.all();

function normaliseTime(raw) {
  if (!raw) return null;
  let t = String(raw).trim().toLowerCase();
  // 4.15pm -> 4:15pm
  t = t.replace(/\./g, ':');
  // 4pm -> 4:00pm  |  4:5pm -> 4:05pm
  t = t.replace(/^(\d{1,2})(?::?(\d{1,2}))?\s*(am|pm)?$/, (_, h, m, mer) => {
    const hh = h.padStart(1, '0');             // keep 1â€“2 digits
    const mm = (m == null ? '00' : m.padStart(2, '0'));
    const meridiem = mer ? mer : '';           // if no am/pm, leave blank (24h not supported here)
    return `${hh}:${mm}${meridiem}`;
  });
  // ensure a space before am/pm: 4:15pm -> 4:15 pm
  t = t.replace(/(am|pm)$/,' $1');
  return t;
}

return rows.map(item => {
  const dateStr = item.json['Publish date'];
  const timeStr = item.json.Time;
  const normTime = normaliseTime(timeStr);

  let scheduledISO = null;
  let shouldPost = false;
  let parseError = null;

  try {
    if (!dateStr || !normTime) throw new Error('Missing date or time');
    // Example: "26 Aug 2025 4:15 pm GMT+0530"
    const full = `${dateStr} ${normTime} GMT+0530`;
    const scheduled = new Date(full);
    if (isNaN(scheduled.getTime())) throw new Error(`Unparseable datetime: "${full}"`);
    scheduled.setSeconds(0, 0);
    scheduledISO = scheduled.toISOString();

    const now = new Date();
    now.setSeconds(0, 0);
    shouldPost = scheduled.getTime() <= now.getTime();
  } catch (e) {
    parseError = e.message;
  }

  return {
    json: {
      ...item.json,
      normalizedTime: normTime,
      scheduledISO,
      shouldPost,
      parseError,          // helpful to see which row failed (null when OK)
    },
  };
});
